<script>import InputWrapper from '../../InputWrapper/InputWrapper.svelte';
import Group from '../../Group/Group.svelte';
import Chip from '../Chip.svelte';
export let use = [], element = undefined, className = '', override = {}, color = undefined, items = [], value = [], label = null, multiple = false, disabled = false, variant = 'outline', size = undefined, radius = undefined, direction = 'row', align = 'flex-start', position = 'left', spacing = 'md';
export { className as class };
function onChanged(item, el) {
    if (el.checked) {
        if (multiple)
            value = [...value, item];
        else
            value = [item];
    }
    else {
        value = value.filter((val) => val !== item);
    }
}
</script>

<!--
@component

A chip group component is a container for Chips.

@see https://svelteui.org/core/chip
@example
    ```svelte
    <ChipGroup bind:value items={items} />
    <ChipGroup label={"Choose your favorite framework"} description={"Choose carefuly"} bind:value={value} items={items} />
    <ChipGroup bind:value={value} items={items} direction={'column'}/>
    <ChipGroup bind:value items={[{label: 'One', value: 1}, {label: 'Two', value: 2}, {label: 'Three', value: 3}]} />
    ```
-->

<InputWrapper bind:element class={className} {label} {override} {size} {...$$restProps}>
	<Group {direction} {spacing} {align} {position}>
		{#each items as item}
			<Chip
				{use}
				label={item.label}
				value={item.value}
				checked={value.includes(item.value)}
				{radius}
				{size}
				{color}
				{variant}
				{disabled}
				on:change={(e) => onChanged(item.value, e.target)}
			/>
		{/each}
	</Group>
</InputWrapper>
