<script>import useStyles from './Text.styles';
import { TextErrors } from './Text.errors';
import Error from '../../internal/errors/Error.svelte';
import { Box } from '../Box';
import { createEventForwarder, useActions } from '../../internal';
import { get_current_component } from 'svelte/internal';
export let use = [], element = undefined, className = '', override = {}, align = 'left', color = 'dark', root = undefined, transform = 'none', variant = 'text', size = 'md', weight = 'normal', gradient = { from: 'indigo', to: 'cyan', deg: 45 }, inline = true, lineClamp = undefined, underline = false, inherit = false, href = '', tracking = 'normal';
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
// --------------Error Handling-------------------
let observable = false;
let err;
if (gradient === { from: 'indigo', to: 'cyan', deg: 45 } && variant !== 'gradient') {
    observable = true;
    err = TextErrors[0];
}
// --------------End Error Handling-------------------
$: ({ cx, getStyles } = useStyles({
    lineClamp,
    underline,
    inline,
    inherit,
    gradient,
    variant,
    align,
    color,
    transform,
    size,
    weight,
    tracking
}));
</script>

<Error {observable} component="Text" code={err} />

<!--
@component

Display text and links with theme styles.
	
@see https://svelteui.org/core/text
@example
    ```tsx
    <Text size='lg'>This is a text</Text> // standard text component
    <Text size='lg' variant='gradient' gradient={{from: 'blue', to: 'red', deg: 45}}>This is a text with colours</Text> // text with gradient colors
    <Text component={'span'}>Span text</Text> // text being used with a 'span' as wrapper
    <Text component={Code}>Code text</Text> // text being used with the component Code as wrapper
    ```
-->

<Box
	{root}
	bind:element
	use={[forwardEvents, [useActions, use]]}
	class={cx(className, getStyles({ css: override }))}
	href={href ?? undefined}
	{...$$restProps}
>
	<slot />
</Box>
